        {
          "text":
            "Your <code>img</code> tag should have a <code>height</code> set to auto.",
          "testString":
            "assert(code.match(/height:\\s*?auto;/g), 'Your <code>img</code> tag should have a <code>height</code> set to auto.');"
        }
